import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../services/student.service';

@Component({
  selector: 'app-edit-student',
  templateUrl: './edit-student.component.html',
  styleUrls: ['./edit-student.component.css']
})
export class EditStudentComponent implements OnInit {
  studentForm: FormGroup; // Define the form group
  constructor(private route: ActivatedRoute, private stdService: StudentService) { }
  ngOnInit() {
    this.route.params.subscribe(params => {
      const studentId = params['id'];
      console.log('Student ID:', studentId);
      // Call the edit method with the student's ID
    this.stdService.edit(studentId).subscribe(data => {
      this.studentForm.patchValue({
        name: data.name,
        email: data.email,
        dob: data.dob,
        gender: data.gender,
        address: data.address,
      });
    });
    });

  }




}
