import { Component, OnInit } from '@angular/core';
import { StudentService } from '../services/student.service';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.css']
})
export class StudentListComponent implements OnInit {
  students: any[] = [];

  constructor(private stdService: StudentService,private dialog: MatDialog) { }

  ngOnInit() {
  this.index();
  }

  index() {
    this.stdService.index().subscribe(
      response => {
        this.students = response;
        console.log(response);
      },
      error => {
        console.error('Error fetching data:', error);
      }
    );
  }

  confirmDelete(studentId: number): void {
    const dialogRef = this.dialog.open(ConfirmDeleteDialogComponent, {
      width: '250px',
    });

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        // User confirmed the deletion, send an HTTP DELETE request to your server
        // Example: this.studentService.deleteStudent(studentId).subscribe(response => { ... });
      }
    });
  }



}
