import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { StudentService } from '../services/student.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-edit-student',
  templateUrl: './edit-student.component.html',
  styleUrls: ['./edit-student.component.css']
})
export class EditStudentComponent implements OnInit {
  studentForm!: FormGroup;

  constructor(private route: ActivatedRoute, private stdService: StudentService, private fb: FormBuilder) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      const studentId = params['id'];
      console.log('Student ID:', studentId);

      // Initialize the form group here
      this.studentForm = this.fb.group({
        name: ['', Validators.required],
        email: ['', Validators.required],
        dob: [''],
        gender: [''],
        address: ['']
      });





      // Fetch student data and then patch the values
      this.stdService.edit(studentId).subscribe(data => {

        const date = new Date(data.dob);
        const formattedDate = date.toISOString().split('T')[0]; // Extract the date portion

        console.log(formattedDate)

        console.log("student data: ", data);
        this.studentForm.patchValue({
          name: data.name,
          email: data.email,
          dob: formattedDate,
          gender: data.gender,
          address: data.address
        });
      });
    });
  }

  onSubmit() {
    if (this.studentForm.valid) {
      const formData = this.studentForm.value;
      console.log(formData);
      // Call your service to update the student data
    }
  }
}
